  static String getReceiptContent({required Expense data}) {
    String txt = "";
    String img = "";
    String expType = "Зал";
    String footer = globals.settings!.text_footer.replaceAll("\n", "<br>");
    switch (data.order_type) {
      case "delivery":
        expType = "Доставка";
        break;
      case "take":
        expType = "Собой";
        break;
      case "book_table":
        expType = "Зал";
        break;
    }
    int sum = 0;
    var barcode = buildBarcode(
      Barcode.code128(useCode128B: false, useCode128C: false),
      data.id.toString(),
    );
    data.order.forEach((element) {
      double currentSum = element.amount * element.product!.price!;
      sum += (currentSum / 100).ceil() * 100;
      txt += """
            <p class="full-width inline-block font1" style='border-bottom:1px solid #000'>
                <span class="left" style='width: 60%'>${element.product!.name}</span>
                <span class="left" style='width: 15%'>${element.amount}</span>
                <span class="right" style='wid th: 25%'> &nbsp; &nbsp; ${(element.amount * element.product!.price!.toDouble()).toInt()}</span>
            </p>
            """;
    });
    if (globals.settings!.logo_image != null) {
      img +=
          """<div class="logo" style="width: 100%; height: 200px; background-image: url(${globals.settings!.logo_image}); background-position:center;background-size:contain; background-repeat: no-repeat;">
                </div>""";
    }
    return """

<style>
    body,
    p {
        font-family: Arial;
        margin: 0px;
        padding: 0px;
    }
    
    body {
        background: #eee;
        width: 576px;
        font-size: 1.8em;
    }
    
    .logo {
        width: 100%;
        background-position: center;
        /* Center the image */
        background-repeat: no-repeat;
        /* Do not repeat the image */
        background-size: cover;
    }
    
    .logo img {
        width: 100%;
    }
    
    .receipt {
        max-width: 576px;
        margin: auto;
        background: white;
    }
    
    .container {
        padding: 5px 0px;
    }
    
    hr {
        border-top: 1px dashed black;
    }
    
    .text-center {
        text-align: center;
    }
    
    .text-left {
        text-align: left;
    }
    
    .text-right {
        text-align: left;
    }
    
    .text-justify {
        text-align: justify;
    }
    
    .right {
        float: right;
    }
    
    .left {
        float: left;
    }
    
    .total {
        font-size: 2.5em;
        margin: 5px;
    }
    
    a {
        color: #1976d2;
    }
    
    span {
        color: black;
    }
    
    .full-width {
        width: 100%;
    }
    
    .inline-block {
        display: inline-block;
    }
    .font1{
      font-size: 0.8em;
    }
    .font2{
      font-size: 1.2em;
    }
    .font3{
      font-size: 1.4em;
    }
    .font4{
      font-size: 1.6em;
    }
    .font5{
      font-size: 1.8em;
    }
    h2{
        margin-bottom:0;
    }
</style>

<body>
$img

    <div class="receipt">
        <div class="container">
            <!-- header part -->
            <div class="text-center">
                <h2>${globals.settings!.cafe_name}</h2>
                <p>Счет № <b style='font-size: 50px'>${data.num}</b></p>
            </div>
            <hr>
            <!-- end header part -->
            <div class="text-center" style='width: 60%; margin: 0 auto;'>
               
            ${barcode}
            </div>
            <p class="full-width inline-block font1">
                <span class=" ">Тип заказа: <b>$expType</b></span> <br>
                ${data.table != null ? """<span class=" ">Cтол №: <b>${data.table!.name}</b></span> <br>""" : ""}
                ${data.delivery != null ? """<span class=" ">Телефон: <b>${data.delivery!.phone}</b></span> <br>
                <span class=" ">Время доставки: <b>${data.delivery!.delivery_time}</b></span> <br>
                <span class=" ">Адрес: <b>${data.delivery!.address}</b></span> <br>""" : ""}
                <span class=" ">Открыт: <b>${DateFormat('dd.MM.yyyy HH:mm:ss').format(DateTime.parse(data.order_date))}</b></span> <br>
                <span class=' '>Распечатано: <b>${DateFormat('dd.MM.yyyy HH:mm:ss').format(DateTime.now())}</b></span><br>
                <span class=" ">Ответственный: <b>${data.employee.name}</b></sp>
            </p>
            <hr>
            
            <p class="full-width inline-block font1" style='border-bottom:1px solid #000'>
                <span class="left" style='width: 60%'>Наименование</span>
                <span class="left" style='width: 15%'>Кол-во</span>
                <span class="right" style='width: 25%'> &nbsp; &nbsp; Сумма</span>
            </p>
            <!-- product part -->
            $txt
            

            <!-- end product part -->

            <!-- footer part -->
            <p class="full-width inline-block font2">
                <b class="left">Сумма заказа:</b>
                <b class="right">$sum</b>
            </p>
            ${(globals.isKassa) ? "" : """<p class="full-width inline-block">
                <b class="left">Облуживаниe ${globals.settings!.percent}%: </b>
                <b class="right">${(sum / 100 * int.parse(globals.settings!.percent)).toInt()}</b>
            </p>"""}
            <b class="full-width inline-block text-center font5">
                Итого: ${data.expense_sum}
            </b>
            <hr>
            <b>
            <p class="full-width inline-block text-center">
                $footer
            </p>
        </b>
        </div>
    </div>
    
</body>

</html>  



  """;
  }

